from Crypto.Util.number import getPrime, bytes_to_long
from secret import flag

p = getPrime(512)
q = getPrime(512)
r = getPrime(512)
n = p * q * r
nonsense = pow(p + 2020, 2020, n)
c = pow(bytes_to_long(flag.encode()), 65537, n)

print("flag_len =", len(flag))
print("n =", n)
print("nonsense =", nonsense)
print("c =", c)

